package day19

import (
	"testing"

	"github.com/sogard-dev/advent-of-code-2015/utils"
	"github.com/stretchr/testify/require"
)

func TestPart1(t *testing.T) {
	testRules := `H => HO
H => OH
O => HH`
	require.Equal(t, 4, part1(testRules, `HOH`))
	require.Equal(t, 7, part1(testRules, `HOHOHO`))
	require.Equal(t, 518, part1(utils.GetInput(t, "input.txt"), ``))
}

func TestPart2(t *testing.T) {
	testRules := `e => H
e => O
H => HO
H => OH
O => HH`
	require.Equal(t, 3, part2(testRules, "HOH"))
	require.Equal(t, 6, part2(testRules, "HOHOHO"))
	//require.Equal(t, 200, part2(utils.GetInput(t, "input.txt"), ``))
}
